    private void performCalculation(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_performCalculation
        if(permutationEdit1.getModel().getLength()==permutationEdit2.getModel().getLength()) {
            PermutationElement p=permutationEdit1.getModel().getValue();
            p.composeOnLeftWith(permutationEdit2.getModel().getValue());
            permutationEdit3.getModel().setValue(p);
            permutationEdit3.repaint();
        }
    }//GEN-LAST:event_performCalculation

